package io.gravitee.rest.api.service.impl;

import static org.assertj.core.api.Assertions.assertThat;
import static org.mockito.ArgumentMatchers.*;
import static org.mockito.Mockito.doNothing;
import static org.mockito.Mockito.never;
import static org.mockito.Mockito.verify;
import static org.mockito.Mockito.when;

import io.gravitee.rest.api.model.parameters.ParameterReferenceType;
import io.gravitee.rest.api.service.EmailNotification;
import io.gravitee.rest.api.service.ParameterService;
import io.gravitee.rest.api.service.notification.NotificationTemplateService;
import io.gravitee.rest.api.service.spring.GraviteeJavaMailManager;
import java.io.File;
import java.util.List;
import java.util.Map;
import javax.mail.internet.InternetAddress;
import javax.mail.internet.MimeMessage;
import org.apache.commons.mail.util.MimeMessageParser;
import org.jetbrains.annotations.NotNull;
import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.ArgumentCaptor;
import org.mockito.Mock;
import org.mockito.Spy;
import org.mockito.junit.MockitoJUnitRunner;
import org.springframework.mail.javamail.JavaMailSender;
import org.springframework.mail.javamail.JavaMailSenderImpl;

@RunWith(MockitoJUnitRunner.class)
public class EmailServiceTest {

    private EmailServiceImpl service;

    @Mock
    private GraviteeJavaMailManager mailManager;

    @Mock
    private NotificationTemplateService notificationTemplateService;

    @Mock
    private ParameterService parameterService;

    @Spy
    private JavaMailSender mailSender = new JavaMailSenderImpl();

    @Before
    public void setUp() throws Exception {
        File file = new File("src/test/resources/template");
        service = new EmailServiceImpl(mailManager, notificationTemplateService, parameterService, file.getCanonicalPath());

        Map<String, List<String>> mailParameters = Map.of("email.enabled", List.of("true"), "email.subject", List.of("[Gravitee.io] %s"));
        when(parameterService.findAll(anyList(), anyString(), any(ParameterReferenceType.class))).thenReturn(mailParameters);

        doNothing().when(mailSender).send(any(MimeMessage.class));

        when(mailManager.getOrCreateMailSender(any(), any())).thenReturn(mailSender);
    }

    @Test
    public void shouldSendEmailNotification() throws Exception {
        String emailTemplate = getEmailTemplateWithImage("images/GRAVITEE_LOGO_RVB-11.png");
        when(notificationTemplateService.resolveTemplateWithParam(eq("template.EMAIL.TITLE"), anyMap())).thenReturn("Test email title");
        when(notificationTemplateService.resolveTemplateWithParam(eq("template.EMAIL"), anyMap())).thenReturn(emailTemplate);

        service.sendEmailNotification(getEmailNotification());

        ArgumentCaptor<MimeMessage> mimeMessageCaptor = ArgumentCaptor.forClass(MimeMessage.class);

        verify(mailSender).send(mimeMessageCaptor.capture());

        MimeMessage mimeMessage = mimeMessageCaptor.getValue();
        MimeMessageParser mimeMessageParser = new MimeMessageParser(mimeMessage).parse();

        assertThat(mimeMessageParser.getFrom()).contains("sender@gravitee.io");
        assertThat(mimeMessageParser.getTo()).contains(new InternetAddress("test@gravitee.io"));
        assertThat(mimeMessageParser.getBcc()).contains(new InternetAddress("copy@gravitee.io"));
        assertThat(mimeMessageParser.getSubject()).isEqualTo("[Gravitee.io] Test email title");
        assertThat(mimeMessageParser.getHtmlContent()).contains("cid:images/GRAVITEE_LOGO_RVB-11.png");
        assertThat(mimeMessageParser.getAttachmentList()).hasSize(1);
        assertThat(mimeMessageParser.getAttachmentList().get(0).getName()).isEqualTo("inline");
        assertThat(mimeMessageParser.getAttachmentList().get(0).getContentType()).isEqualTo("image/png");
    }

    @Test
    public void shouldSendEmailNotificationWithoutImage() throws Exception {
        String emailTemplateWithHackyImage = getEmailTemplateWithImage("../../images/image_user_shouldnt_access.png");

        when(notificationTemplateService.resolveTemplateWithParam(eq("template.EMAIL.TITLE"), anyMap())).thenReturn("Test email title");
        when(notificationTemplateService.resolveTemplateWithParam(eq("template.EMAIL"), anyMap())).thenReturn(emailTemplateWithHackyImage);

        service.sendEmailNotification(getEmailNotification());

        ArgumentCaptor<MimeMessage> mimeMessageCaptor = ArgumentCaptor.forClass(MimeMessage.class);

        verify(mailSender).send(mimeMessageCaptor.capture());

        MimeMessage mimeMessage = mimeMessageCaptor.getValue();
        MimeMessageParser mimeMessageParser = new MimeMessageParser(mimeMessage).parse();

        assertThat(mimeMessageParser.getFrom()).contains("sender@gravitee.io");
        assertThat(mimeMessageParser.getTo()).contains(new InternetAddress("test@gravitee.io"));
        assertThat(mimeMessageParser.getBcc()).contains(new InternetAddress("copy@gravitee.io"));
        assertThat(mimeMessageParser.getSubject()).isEqualTo("[Gravitee.io] Test email title");
        assertThat(mimeMessageParser.getAttachmentList()).isEmpty();
    }

    @Test
    public void shouldSendEmailNotificationWithBase64Image() throws Exception {
        String graviteeLogoInBase64 =
            "data:image/png;base64,";
        String emailTemplate = getEmailTemplateWithImage(graviteeLogoInBase64);
        when(notificationTemplateService.resolveTemplateWithParam(eq("template.EMAIL.TITLE"), anyMap())).thenReturn("Test email title");
        when(notificationTemplateService.resolveTemplateWithParam(eq("template.EMAIL"), anyMap())).thenReturn(emailTemplate);

        service.sendEmailNotification(getEmailNotification());

        ArgumentCaptor<MimeMessage> mimeMessageCaptor = ArgumentCaptor.forClass(MimeMessage.class);

        verify(mailSender).send(mimeMessageCaptor.capture());

        MimeMessage mimeMessage = mimeMessageCaptor.getValue();
        MimeMessageParser mimeMessageParser = new MimeMessageParser(mimeMessage).parse();

        assertThat(mimeMessageParser.getFrom()).contains("sender@gravitee.io");
        assertThat(mimeMessageParser.getTo()).contains(new InternetAddress("test@gravitee.io"));
        assertThat(mimeMessageParser.getBcc()).contains(new InternetAddress("copy@gravitee.io"));
        assertThat(mimeMessageParser.getSubject()).isEqualTo("[Gravitee.io] Test email title");
        assertThat(mimeMessageParser.getHtmlContent()).contains("cid:" + graviteeLogoInBase64);
        assertThat(mimeMessageParser.getAttachmentList()).hasSize(1);
        assertThat(mimeMessageParser.getAttachmentList().get(0).getName()).isEqualTo("inline");
        assertThat(mimeMessageParser.getAttachmentList().get(0).getContentType()).isEqualTo("image/png");
    }

    @NotNull
    private String getEmailTemplateWithImage(String image) {
        return (
            "<html>\n" +
            "\t<body style=\"text-align: center;\">\n" +
            "\t\t<header>\n" +
            "\t\t\t<img src=\"" +
            image +
            "\" />\n" +
            "\t\t</header>\n" +
            "\t\t<div style=\"margin-top: 50px; color: #424e5a;\">\n" +
            "\t\t\t<p>\n" +
            "\t\t\t\t${message}\n" +
            "\t\t\t</p>\n" +
            "\t\t</div>\n" +
            "\t</body>\n" +
            "</html>"
        );
    }

    @Test
    public void shouldNotSendEmailNotification_WhenEmailIsDisabled() {
        when(parameterService.findAll(anyList(), anyString(), any(ParameterReferenceType.class)))
            .thenReturn(Map.of("email.enabled", List.of("false")));

        service.sendEmailNotification(getEmailNotification());

        verify(mailSender, never()).send(any(MimeMessage.class));
    }

    @NotNull
    private EmailNotification getEmailNotification() {
        EmailNotification emailNotification = new EmailNotification();
        emailNotification.setFrom("sender@gravitee.io");
        emailNotification.setTo("test@gravitee.io");
        emailNotification.setTemplate("template");
        emailNotification.setBcc(new String[] { "copy@gravitee.io" });
        return emailNotification;
    }
}
