package io.gravitee.rest.api.service.impl;

import static org.assertj.core.api.Assertions.assertThat;
import static org.assertj.core.api.Assertions.tuple;
import static org.mockito.ArgumentMatchers.any;
import static org.mockito.ArgumentMatchers.anyList;
import static org.mockito.ArgumentMatchers.anyMap;
import static org.mockito.ArgumentMatchers.anyString;
import static org.mockito.ArgumentMatchers.eq;
import static org.mockito.Mockito.lenient;
import static org.mockito.Mockito.never;
import static org.mockito.Mockito.verify;
import static org.mockito.Mockito.when;

import io.gravitee.rest.api.mail.MimeMessageParser;
import io.gravitee.rest.api.model.parameters.ParameterReferenceType;
import io.gravitee.rest.api.service.ParameterService;
import io.gravitee.rest.api.service.builder.EmailNotificationBuilder;
import io.gravitee.rest.api.service.common.ExecutionContext;
import io.gravitee.rest.api.service.common.GraviteeContext;
import io.gravitee.rest.api.service.notification.NotificationTemplateService;
import io.gravitee.rest.api.service.spring.GraviteeJavaMailManager;
import jakarta.activation.DataSource;
import jakarta.mail.internet.InternetAddress;
import jakarta.mail.internet.MimeMessage;
import java.io.File;
import java.util.List;
import java.util.Map;
import org.jetbrains.annotations.NotNull;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.ArgumentCaptor;
import org.mockito.Mock;
import org.mockito.Spy;
import org.mockito.junit.jupiter.MockitoExtension;
import org.springframework.mail.javamail.JavaMailSender;
import org.springframework.mail.javamail.JavaMailSenderImpl;

@ExtendWith(MockitoExtension.class)
public class EmailServiceTest {

    private final ExecutionContext EXECUTION_CONTEXT = new ExecutionContext("DEFAULT", "DEFAULT");
    private final EmailNotificationBuilder.EmailTemplate TEMPLATE = EmailNotificationBuilder.EmailTemplate.API_API_STARTED;

    private EmailServiceImpl service;

    @Mock
    private GraviteeJavaMailManager mailManager;

    @Mock
    private NotificationTemplateService notificationTemplateService;

    @Mock
    private ParameterService parameterService;

    @Spy
    private JavaMailSender mailSender = new JavaMailSenderImpl();

    @BeforeEach
    public void setUp() throws Exception {
        service = new EmailServiceImpl(
            mailManager,
            notificationTemplateService,
            parameterService,
            new File("src/test/resources/template").getCanonicalPath()
        );

        lenient()
            .when(parameterService.findAll(anyList(), anyString(), any(ParameterReferenceType.class), any(ExecutionContext.class)))
            .thenReturn(Map.of("email.enabled", List.of("true"), "email.subject", List.of("[Gravitee.io] %s")));

        lenient().doNothing().when(mailSender).send(any(MimeMessage.class));

        lenient().when(mailManager.getOrCreateMailSender(any(), any(), any())).thenReturn(mailSender);
    }

    @Test
    public void should_send_email_notification() throws Exception {
        when(
            notificationTemplateService.resolveTemplateWithParam(
                eq(GraviteeContext.getCurrentOrganization()),
                eq("API.API_STARTED.EMAIL.TITLE"),
                anyMap()
            )
        ).thenReturn("Test email title");
        when(
            notificationTemplateService.resolveTemplateWithParam(
                eq(GraviteeContext.getCurrentOrganization()),
                eq("API.API_STARTED.EMAIL"),
                anyMap()
            )
        ).thenReturn(buildEmailTemplateWithImage("images/GRAVITEE_LOGO_RVB-11.png"));

        service.sendEmailNotification(EXECUTION_CONTEXT, anEmailNotification().build());

        ArgumentCaptor<MimeMessage> mimeMessageCaptor = ArgumentCaptor.forClass(MimeMessage.class);
        verify(mailSender).send(mimeMessageCaptor.capture());

        MimeMessageParser mimeMessageParser = new MimeMessageParser(mimeMessageCaptor.getValue()).parse();
        assertThat(mimeMessageParser.getFrom()).contains("sender@gravitee.io");
        assertThat(mimeMessageParser.getTo()).containsExactly(new InternetAddress("test@gravitee.io"));
        assertThat(mimeMessageParser.getBcc()).containsExactly(new InternetAddress("copy@gravitee.io"));
        assertThat(mimeMessageParser.getSubject()).isEqualTo("[Gravitee.io] Test email title");
        assertThat(mimeMessageParser.getHtmlContent()).contains("cid:images/GRAVITEE_LOGO_RVB-11.png");
        assertThat(mimeMessageParser.getAttachmentList())
            .extracting(DataSource::getName, DataSource::getContentType)
            .containsExactly(tuple("inline", "image/png"));
    }

    @Test
    public void should_send_email_notification_with_only_bcc_defined_as_recipient() throws Exception {
        when(
            notificationTemplateService.resolveTemplateWithParam(
                eq(GraviteeContext.getCurrentOrganization()),
                eq("API.API_STARTED.EMAIL.TITLE"),
                anyMap()
            )
        ).thenReturn("Test email title");
        when(
            notificationTemplateService.resolveTemplateWithParam(
                eq(GraviteeContext.getCurrentOrganization()),
                eq("API.API_STARTED.EMAIL"),
                anyMap()
            )
        ).thenReturn(buildEmailTemplateWithImage("images/GRAVITEE_LOGO_RVB-11.png"));

        service.sendEmailNotification(EXECUTION_CONTEXT, anEmailNotification().to((String[]) null).build());

        ArgumentCaptor<MimeMessage> mimeMessageCaptor = ArgumentCaptor.forClass(MimeMessage.class);
        verify(mailSender).send(mimeMessageCaptor.capture());

        MimeMessageParser mimeMessageParser = new MimeMessageParser(mimeMessageCaptor.getValue()).parse();
        assertThat(mimeMessageParser.getTo()).isEmpty();
        assertThat(mimeMessageParser.getBcc()).containsExactly(new InternetAddress("copy@gravitee.io"));
    }

    @Test
    public void should_send_email_notification_to_sender_if_requested() throws Exception {
        when(
            notificationTemplateService.resolveTemplateWithParam(
                eq(GraviteeContext.getCurrentOrganization()),
                eq("API.API_STARTED.EMAIL.TITLE"),
                anyMap()
            )
        ).thenReturn("Test email title");
        when(
            notificationTemplateService.resolveTemplateWithParam(
                eq(GraviteeContext.getCurrentOrganization()),
                eq("API.API_STARTED.EMAIL"),
                anyMap()
            )
        ).thenReturn(buildEmailTemplateWithImage("images/GRAVITEE_LOGO_RVB-11.png"));

        service.sendEmailNotification(EXECUTION_CONTEXT, anEmailNotification().copyToSender(true).build());

        ArgumentCaptor<MimeMessage> mimeMessageCaptor = ArgumentCaptor.forClass(MimeMessage.class);
        verify(mailSender).send(mimeMessageCaptor.capture());

        MimeMessageParser mimeMessageParser = new MimeMessageParser(mimeMessageCaptor.getValue()).parse();
        assertThat(mimeMessageParser.getTo()).containsExactly(new InternetAddress("test@gravitee.io"));
        assertThat(mimeMessageParser.getBcc()).containsExactly(
            new InternetAddress("copy@gravitee.io"),
            new InternetAddress("sender@gravitee.io")
        );
    }

    @Test
    public void should_send_email_notification_without_image_when_image_path_is_incorrect() throws Exception {
        when(
            notificationTemplateService.resolveTemplateWithParam(
                eq(GraviteeContext.getCurrentOrganization()),
                eq("API.API_STARTED.EMAIL.TITLE"),
                anyMap()
            )
        ).thenReturn("Test email title");
        when(
            notificationTemplateService.resolveTemplateWithParam(
                eq(GraviteeContext.getCurrentOrganization()),
                eq("API.API_STARTED.EMAIL"),
                anyMap()
            )
        ).thenReturn(buildEmailTemplateWithImage("../../images/image_user_shouldnt_access.png"));

        service.sendEmailNotification(EXECUTION_CONTEXT, anEmailNotification().build());

        ArgumentCaptor<MimeMessage> mimeMessageCaptor = ArgumentCaptor.forClass(MimeMessage.class);
        verify(mailSender).send(mimeMessageCaptor.capture());

        MimeMessageParser mimeMessageParser = new MimeMessageParser(mimeMessageCaptor.getValue()).parse();
        assertThat(mimeMessageParser.getAttachmentList()).isEmpty();
    }

    @Test
    public void should_send_email_notification_with_base_64_image() throws Exception {
        String graviteeLogoInBase64 =
            "data:image/png;base64,";
        when(
            notificationTemplateService.resolveTemplateWithParam(
                eq(EXECUTION_CONTEXT.getOrganizationId()),
                eq("API.API_STARTED.EMAIL.TITLE"),
                anyMap()
            )
        ).thenReturn("Test email title");
        when(
            notificationTemplateService.resolveTemplateWithParam(
                eq(EXECUTION_CONTEXT.getOrganizationId()),
                eq("API.API_STARTED.EMAIL"),
                anyMap()
            )
        ).thenReturn(buildEmailTemplateWithImage(graviteeLogoInBase64));

        service.sendEmailNotification(EXECUTION_CONTEXT, anEmailNotification().build());

        ArgumentCaptor<MimeMessage> mimeMessageCaptor = ArgumentCaptor.forClass(MimeMessage.class);

        verify(mailSender).send(mimeMessageCaptor.capture());

        MimeMessageParser mimeMessageParser = new MimeMessageParser(mimeMessageCaptor.getValue()).parse();
        assertThat(mimeMessageParser.getHtmlContent()).contains("cid:" + graviteeLogoInBase64);
        assertThat(mimeMessageParser.getAttachmentList())
            .extracting(DataSource::getName, DataSource::getContentType)
            .containsExactly(tuple("inline", "image/png"));
    }

    @Test
    public void should_not_send_email_notification_when_email_is_disabled() {
        when(parameterService.findAll(anyList(), anyString(), any(ParameterReferenceType.class), any(ExecutionContext.class))).thenReturn(
            Map.of("email.enabled", List.of("false"))
        );

        service.sendEmailNotification(EXECUTION_CONTEXT, anEmailNotification().build());

        verify(mailSender, never()).send(any(MimeMessage.class));
    }

    @Test
    public void should_not_send_email_notification_when_no_recipient() {
        service.sendEmailNotification(EXECUTION_CONTEXT, anEmailNotification().to().bcc().build());

        verify(mailSender, never()).send(any(MimeMessage.class));
    }

    @NotNull
    private String buildEmailTemplateWithImage(String image) {
        return (
            "<html>\n" +
            "\t<body style=\"text-align: center;\">\n" +
            "\t\t<header>\n" +
            "\t\t\t<img src=\"" +
            image +
            "\" />\n" +
            "\t\t</header>\n" +
            "\t\t<div style=\"margin-top: 50px; color: #424e5a;\">\n" +
            "\t\t\t<p>\n" +
            "\t\t\t\t${message}\n" +
            "\t\t\t</p>\n" +
            "\t\t</div>\n" +
            "\t</body>\n" +
            "</html>"
        );
    }

    private EmailNotificationBuilder anEmailNotification() {
        return new EmailNotificationBuilder().template(TEMPLATE).from("sender@gravitee.io").to("test@gravitee.io").bcc("copy@gravitee.io");
    }
}
